import { Mark } from '@tiptap/core';

const Highlight = Mark.create({
  name: 'highlight',

  parseHTML() {
    return [
      {
        tag: 'mark',
      },
    ];
  },

  renderHTML() {
    return ['mark', {}, 0];
  },

  addCommands() {
    return {
      toggleHighlight: () => ({ commands }) => {
        return commands.toggleMark('highlight');
      },
    };
  },
});

export default Highlight;
