import { Node } from "@tiptap/core";

const ImageResizer = Node.create({
  name: "imageResizer",

  // Add an image to the document
  group: "inline",
  inline: true,
  atom: true,

  addAttributes() {
    return {
      src: {
        default: null,
      },
      alt: {
        default: null,
      },
      width: {
        default: "auto",
      },
      height: {
        default: "auto",
      },
      alignment: {
        default: "center", // Default alignment
      },
    };
  },

  parseHTML() {
    return [
      {
        tag: "img",
      },
    ];
  },

  renderHTML({ node }) {
    return [
      "img",
      {
        src: node.attrs.src,
        alt: node.attrs.alt,
        width: node.attrs.width,
        height: node.attrs.height,
        style: `text-align: ${node.attrs.alignment};`,
      },
    ];
  },

  addCommands() {
    return {
      setImageAlignment: (alignment) => ({ commands }) => {
        return commands.updateAttributes("imageResizer", {
          alignment,
        });
      },
      resizeImage: (width, height) => ({ commands }) => {
        return commands.updateAttributes("imageResizer", {
          width,
          height,
        });
      },
    };
  },
});

export default ImageResizer;
