import React, { useState } from "react";
import { Editor } from "@tiptap/react";
import { StarterKit } from "@tiptap/starter-kit";
import ImageResizer from "./ImageResizerExtension"; // Import the extension
import { EditorContent, useEditor } from "@tiptap/react";

const TiptapEditor = () => {
  const editor = useEditor({
    extensions: [
      StarterKit,
      ImageResizer, // Add the custom extension
    ],
    content: `<p>Hello, world!</p>`,
  });

  // Command to align image
  const alignImage = (alignment) => {
    editor.commands.setImageAlignment(alignment);
  };

  // Command to resize image
  const resizeImage = (width, height) => {
    editor.commands.resizeImage(width, height);
  };

  return (
    <div>
      <div>
        <button onClick={() => alignImage("left")}>Left Align</button>
        <button onClick={() => alignImage("center")}>Center Align</button>
        <button onClick={() => alignImage("right")}>Right Align</button>

        <button onClick={() => resizeImage("300", "auto")}>Resize to 300px</button>
        <button onClick={() => resizeImage("auto", "auto")}>Reset Size</button>
      </div>

      <EditorContent editor={editor} />
    </div>
  );
};

export default TiptapEditor;
